import React from 'react';
import '../css/Facebook.css';
import LeftCard from './Layout/LeftCard';
import withAuth from '../util/withAuth';
import $ from 'jquery';
import FB from 'fb';
import Twit from 'twit';
import { TwitterTimelineEmbed, TwitterShareButton, TwitterFollowButton, TwitterHashtagButton, TwitterMentionButton, TwitterTweetEmbed, TwitterMomentShare, TwitterDMButton, TwitterVideoEmbed, TwitterOnAirButton } from 'react-twitter-embed';


class Facebook extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            accessToken: '',
            email: '',
            name: '',
            userID: '',
            pictureUrl: ''
        }

        this.T = new Twit({
            consumer_key: 'todfC8BjhF9MbQ7VUeGY8EyWH',
            consumer_secret: 'ftDjrAI9KMaZOtYWpg0sZWGx6lqIq4Jhan7uokwMdC2yKHbDj2',
            access_token: '1180431317592354816-LIebKtNfHta2Le3KB53RukXEZoGwrs',
            access_token_secret: 'mfxg0EiFnq7pv49kpoCgbArupSMqzTA1MBIBqU9FC6YxX',
            timeout_ms: 60*1000,
            strictSSL: true,
        })
    }

    componentDidMount() {
        window.twttr.widgets.load();
        let jwtToken = localStorage.getItem("JwtToken");

        fetch(`/getuserdata/${jwtToken}`, {
            method: 'GET',
            headers: {
              'content-type': 'application/json'
            }
        })
        .then(response => response.json())
        .then(jsonData => {
            this.setState({
                accessToken: jsonData.accessToken,
                email: jsonData.email,
                name: jsonData.name,
                userID: jsonData.userID,
                pictureUrl: jsonData.pictureUrl
            }, () => {
                // fetch(`https://graph.facebook.com/${this.state.userID}/posts?fields=id,name,full_picture,message,place,created_time&access_token=${this.state.accessToken}`, {
                //     method: 'GET'
                // }).then(response => response.json())
                // .then(jsonData => {

                // });
            });
        }); 
    }

    render() {
        return (
            <div className="facebook-container">
                <div className="fb-grid-container">
                    <div className="fb-grid-item-1">
                        <div className="categories-container">
                            <div className="profil-container">
                                <div className="ins-profil-container">
                                    <img 
                                        src={this.state.pictureUrl} 
                                        alt="Profil"
                                        className="fb-profil-picture"
                                    />
                                    { this.state.name }
                                </div>
                            </div>
                            <hr className="hr-margin" />
                            <LeftCard />
                        </div>
                    </div>
                    <div className="feed-container">
                        <div className="feed-add-container">
                            <i className="fas fa-plus fa-3x plus-shadow"></i>
                        </div>
                        {/* <a class="twitter-timeline" href="https://twitter.com/TwitterDev/likes?ref_src=twsrc%5Etfw">Tweets Liked by @TwitterDev</a> */}
                        <TwitterTweetEmbed
                            tweetId={'933354946111705097'}
                        />

                    </div>
                    <div className="fb-grid-item-3">
                        <div className="rl-container">
                            <div className="groups-container"></div>
                        </div>
                        <div className="rr-container">
                            <div className="friends-container"></div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withAuth(Facebook);
