import decode from 'jwt-decode';
import Cookies from 'universal-cookie';

export default class AuthService {

    setToken(idToken) {
        localStorage.setItem("JwtToken", idToken);
    }

    getToken() {
        return localStorage.getItem("JwtToken");
    }

    getTwitterToken() {
        let cookie = new Cookies();
        return cookie.get("twitterJwtToken");
    }

    isTokenExpired(token) {
        try {
            const decoded = decode(token);

            if(decoded.exp < Date.now() / 1000) {
                return true;
            } else {
                return false;
            }
        } catch(error) {
            return false;
        }
    }

    loggedIn() {
        let twitterJwtToken = this.getToken();
        let facebookJwtToken = this.getTwitterToken();

        if(this.getToken() !== null) {
            const token = this.getToken();
            return !!token && !this.isTokenExpired(token);
        }

        if(this.getTwitterToken() !== null) {
            const twitterToken = this.getTwitterToken();
            return !!twitterToken && !this.isTokenExpired(twitterToken);
        }
        return false;
    }

    logout() {
        localStorage.removeItem("JwtToken");
    }

    logoutTwitter() {
        let cookie = new Cookies();
        cookie.remove("twitterJwtToken");
    }
}
