import React from 'react';
import AuthService from './AuthService';
import TwitterExplore from '../components/twitter/TwitterExplore';

export default function withAuth(AuthComponent) {
    const Auth =  new AuthService();
    let customAuthComponent = false;

    class AuthWrapped extends React.Component {
        componentDidMount() {
            customAuthComponent = true;

            if(!Auth.loggedIn()) {
                this.props.history.replace("/login");
            } else {
                let twitterJwtToken = Auth.getTwitterToken();
                let facebookJwtToken = Auth.getToken();
                try {
                    if(twitterJwtToken) {
                        if(this.props.twitterExplore) {
                            customAuthComponent && this.props.history.replace("/twitter/explore");
                        } else {
                            customAuthComponent && this.props.history.replace("/twitter");
                        }
                    }
                    if(facebookJwtToken) {
                        customAuthComponent && this.props.history.replace("/dashboard");
                    }
                } catch(err) {
                    if(twitterJwtToken) {
                        Auth.logoutTwitter();
                    }
                    if(facebookJwtToken) {
                        Auth.logout();
                    }
                    this.props.history.replace("/login");
                }
            }
        }

        componentWillUnmount() {
            customAuthComponent = false;
        }

        render() {
            if(Auth.loggedIn()) {
                return (
                    customAuthComponent && <AuthComponent history={this.props.history} />
                );
            } else {
                return null;
            }
        }
    }
    return AuthWrapped;
}
