import React from 'react';
import { withTranslation } from 'react-i18next';
import jsPDF from 'jspdf';
import divelogLogo from '../../img/logo.png';
import ConvertTime from '../../util/ConvertTime';
import $ from 'jquery';
import '../../css/PdfLogbook.css';

class PDFTableButton extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            partnerName: '',
            partnerSurname: '',
            marker: {},
            entryTime: '',
            exitTime: '',
            averageDepth: 0.0,
            maxDepth: 0.0,
            visibility: 0.0,
            waterTemperature: 0.0,
            airTemperature: 0.0,
            cylinderCapacity: '',
            divingSuit: 'NONE',
            waterType: 'NONE',
            waterEntryType: 'NONE',
            ballast: 0.0,
            glovesType: 'NONE',
            divingType: 'NONE',
            comment: ''
        }
        this.ConvertTime = new ConvertTime();
        this.onClick = this.onClick.bind(this);
    }

    renderDataForHTML() {
        return (
            <h1>Hubert Struminski</h1>
        );
    }

    onClick(e) {
        e.preventDefault();

        const id = this.props.id;
        let jwtToken = localStorage.getItem("JwtToken");

        fetch(`/pdf/logbook/${id}/${jwtToken}`, {
            method: 'GET',
            headers: {
                'Accept': 'application/json, text/plain, */*',
                'content-type': 'application/json'
            }
        })
        .then(response => response.json())
        .then(jsonData => {
            let time = this.ConvertTime.convertTime(jsonData.entryTime, jsonData.exitTime, true);

            const markerElement = {
                id: jsonData.marker.id,
                name: jsonData.marker.name,
                latitude: jsonData.marker.latitude,
                longitude: jsonData.marker.longitude
            }

            this.setState({
                partnerName: jsonData.partnerName,
                partnerSurname: jsonData.partnerSurname,
                marker: markerElement,
                entryTime: time[0],
                exitTime: time[1],
                averageDepth: jsonData.averageDepth,
                maxDepth: jsonData.maxDepth,
                visibility: jsonData.visibility,
                waterTemperature: jsonData.waterTemperature,
                airTemperature: jsonData.airTemperature,
                cylinderCapacity: jsonData.cylinderCapacity,
                divingSuit: jsonData.divingSuit,
                waterType: jsonData.waterType,
                waterEntryType: jsonData.waterEntryType,
                ballast: jsonData.ballast,
                glovesType: jsonData.glovesType,
                divingType: jsonData.divingType,
                comment: jsonData.comment
            }, () => {
                let doc = new jsPDF({
                    orientation: "p",
                    unit: "mm",
                });
                let img = new Image();
                img.src = divelogLogo;

                var output = this.renderDataForHTML();
                // doc.text(`Imię partnera:  ${this.state.partnerName}`, 10, 10);
                // doc.text(`Nazwisko partnera: ${this.state.partnerSurname}`, 10, 10);
                // doc.addImage(img, 'PNG', 15, 40, 100, 100);
                doc.fromHTML($(".pdf-logbook-container").html(), 20, 20, { 'width': 500, 'elementHandlers': this.specialElementHandlers });

                doc.save("pdf");
            });
        }); 
    }

    specialElementHandlers = {
        '#hubert': function (element, renderer) {
            return true;
        },
        '.controls': function (element, renderer) {
            return true;
        }
    };

    render() {
        return (
            <div>
                <button className="btn btn-primary"
                    onClick={this.onClick}
                >
                    PDF
                </button>

                <div className="pdf-logbook-container" style={{ display: 'none'}}>
                    <div className="pdf-logbook-box">
                        Hubert Struminski  
                        <br />
                        <br />
                        <h5 style={{ color: 'red'}}>Imię partnera: {this.state.partnerName}</h5>
                        <p style={{ color: 'blue'}}>Comment: {this.state.comment}</p>    
                    </div>
                </div>
            </div>
        );
    }
}

export default withTranslation("common")(PDFTableButton);
