import React from 'react';
import '../../../css/twitter-messages/SearchPeopleConversation.css';
import AuthService from '../../../util/AuthService';
import TwitterConversationContact from './TwitterConversationContact';
import $ from 'jquery';
import TwitterMessagesSearch from './TwitterMessagesSearch';

class SearchPeopleConversation extends React.Component {
    constructor() {
        super();

        this.state = {
            conversations: [],
            isConversationsRetrieved: false,
            copyOfConversations: [],
            isLoading: true
        }
        this.Auth = new AuthService();
        this.copyOfConversations = [];
        this.renderConversations = this.renderConversations.bind(this);
        this.searchInList = this.searchInList.bind(this);
        this.retrieveConversations = this.retrieveConversations.bind(this);
        this.onClose = this.onClose.bind(this);
    }

    componentDidMount() {
        let jwtToken = this.Auth.getRightSocialToken();

        fetch(`/twitter/direct/messages/${jwtToken}`, {
            method: 'GET',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
        }).then(response => response.json())
        .then(json => {
            json.map((item, index) => {
                const element = {
                    recipientId: item.recipientId,
                    senderId: item.senderId,
                    name: item.name,
                    screenName: item.screenName,
                    createdAt: item.createdAt,
                    text: item.text,
                    pictureUrl: item.pictureUrl
                }
                this.setState({ conversations: this.state.conversations.concat(element) });
            });
            this.setState({ isConversationsRetrieved: true }, () => {
                this.setState({ isLoading: false });
                this.copyOfConversations = this.state.conversations.map((x) => x);
                $(".twitter-messages-list-persons-spinner").css({ display: "block" });
            });
        })
    }

    renderConversations() {
        return this.state.conversations
            .sort((a, b) => new Date(a.createdAt) - new Date(b.createdAt))
            .map((conversation, index) => {
            return (
                <TwitterConversationContact 
                    recipientId={conversation.recipientId}
                    senderId={conversation.senderId}
                    name={conversation.name}
                    screenName={conversation.screenName}
                    createdAt={conversation.createdAt}
                    text={conversation.text}
                    pictureUrl={conversation.pictureUrl}
                />
            );
        });
    }

    searchInList(searchInput) {
        this.setState({ 
            isConversationsRetrieved: true,
            conversations: this.copyOfConversations.map((x) => x)

        }, () => {
            return this.state.conversations.map((conversation, index) => {
                if(conversation.name.includes(searchInput) || conversation.screenName.includes(searchInput)) {
                    this.setState({ conversations: this.state.conversations.filter((item, i) => i === index)});
                }
            });
        });
        return null;
    }

    retrieveConversations() {
        return this.copyOfConversations
            .sort((a, b) => new Date(a.createdAt) - new Date(b.createdAt))
            .map((conversation, index) => {
            return (
                <TwitterConversationContact 
                    recipientId={conversation.recipientId}
                    senderId={conversation.senderId}
                    name={conversation.name}
                    screenName={conversation.screenName}
                    createdAt={conversation.createdAt}
                    text={conversation.text}
                    pictureUrl={conversation.pictureUrl}
                />
            );
        });
    }

    onClose(e) {
        e.preventDefault();
        this.props.setIsNotVisibleModalToSearch();
    }

    render() {
        return (
            <div className="twitter-messages-search-people-conversation-modal">
                <div className="twitter-messages-search-people-conversation-header">
                    <div className="twitter-messages-search-people-header-left">
                        <i 
                            className="fa fa-times twitter-messages-search-people-modal-close"
                            onClick={this.onClose}
                        ></i>
                        <span style={{ color: 'black', fontWeight: 700, display: 'block', paddingLeft: '10%', width: '100%' }}>New message</span>
                    </div>
                    <div className="twitter-messages-search-people-header-right">
                        <button className="twitter-messages-search-people-modal-btn-next">Next</button>
                    </div>
                </div>
                <input 
                    className="twitter-messages-search-people-input" 
                    type="text" 
                    placeholder="Search people"
                />
                <div className=""></div>
                <div className="twitter-messages-search-people-info-icon-title">
                    <i className="fas fa-user twitter-messages-search-people-icon-user"></i> You follow each other
                </div>
                <div className="twitter-messages-search-people-modal-list-contacts"></div>

            </div>
        );
    }
}

export default SearchPeopleConversation;
