import React from 'react';
import '../../css/GoogleMap.css';
import { Map, Marker, GoogleApiWrapper, InfoWindow } from 'google-maps-react';
import GoogleModal from './GoogleModal';
import $ from 'jquery';
import { fakeAuth } from '../../util/fakeAuth';
import DeleteButton from './DeleteButton';

class GoogleMap extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            isLoading: true,
            showingInfoWindow: false,
            activeMarker: {},
            selectedPlace: {},
            markers: [],
            isFinishMarker: false,
            latitude: '',
            longitude: '',
            markerName: ''
        }
        this.onMapClick = this.onMapClick.bind(this);
        this.setFinishMarker = this.setFinishMarker.bind(this);
    }

    componentDidMount() {
        this.setState({ isLoading: false });

        fetch(`/get/markers/${fakeAuth.userID}`, {
            method: 'GET',
            headers: {
                'Accept': 'application/json, text/plain, */*',
                'content-type': 'application/json'
            }
        })
        .then(response => response.json())
        .then(jsonData => {
            jsonData.map((marker, index) => {
                let element = {
                    name: marker.name,
                    latitude: marker.latitude,
                    longitude: marker.longitude
                }
                this.setState({
                    markers: this.state.markers.concat(element)
                })
            })
        }); 
    }

    showMarkers = () => {
        return this.state.markers.map((marker, index) => {
            return (
                <Marker 
                    key={index} 
                    name={marker.name}
                    position={{ lat: marker.latitude, lng: marker.longitude }}
                    onClick={this.onMarkerClick}
                />
            );
        });
    }

    showTableRows = () => {
        let i = 0;
        return this.state.markers.map((marker, index) => {
            return (
                <tr>
                    <th scope="row">{++i}</th>
                    <td>{marker.name}</td>
                    <td>{marker.latitude}</td>
                    <td>{marker.longitude}</td>
                    <td>
                        <DeleteButton id={marker.id} />
                    </td>
                </tr>
            );
        })
    }

    onMarkerClick = (props, marker, e) => {
        this.setState({
            selectedPlace: props,
            activeMarker: marker,
            showingInfoWindow: true
        });
    }

    onClose = props => {
        if(this.state.showingInfoWindow) {
            this.setState({
                showingInfoWindow: false,
                activeMarker: null
            });
        }
    };

    onMapClick(t, map, coord) {
        const { latLng } = coord;
        const lat = latLng.lat();
        const lng = latLng.lng();

        this.setState({ 
            latitude: lat,
            longitude: lng
        });

        $(document).on('show.bs.modal', "#modalCenter", function (event) {
            $('#name').trigger('focus');
        });
        $("#modalCenter").modal('show');
    }

    setFinishMarker() {
        this.setState({ isFinishMarker: true });
    }

    render() {
        const mapStyle = {
            width: '100%',
            height: '100%',
        }

        let loadingScreen = (
            <div class="d-flex justify-content-center">
                <div class="spinner-grow" role="status">
                    <span class="sr-only">Loading...</span>
                </div>
            </div>
        )

        let map = (
            <div className="google-container">
                <div className="table-container">
                    <div>
                    Your story...
                    <br />
                    <div className="table-responsive">
                        <table className="table table-striped">
                            <thead>
                                <tr>
                                    <th scope="col">#</th>
                                    <th scope="col">Name</th>
                                    <th scope="col">Latitude</th>
                                    <th scope="col">Longitude</th>
                                    <th scope="col">Delete</th>
                                </tr>
                            </thead>
                            <tbody>
                                { this.showTableRows() }
                            </tbody>
                        </table>
                    </div>
                    </div>
                </div>
                <div>
                    <Map
                        google={this.props.google}
                        zoom={5}
                        style={mapStyle}
                        initialCenter={{ lat: 50.087, lng: 14.421}}
                        onClick={this.onMapClick}
                    >
                        { this.showMarkers() }
                        <InfoWindow
                            marker={this.state.activeMarker}
                            visible={this.state.showingInfoWindow}
                            onClose={this.onClose}
                        >
                        <div className="alert alert-success" role="alert">
                            <h4>{this.state.selectedPlace.name}</h4>
                        </div>
                        </InfoWindow>
                    </Map>
                    <GoogleModal 
                        latitude={this.state.latitude}
                        longitude={this.state.longitude}
                        setFinishMarker={this.setFinishMarker}
                    />
                </div>
            </div>

        )
        let content = this.state.isLoading ? loadingScreen : map;

        return (
            <div>
                { content }
            </div>
        );
    }
}

export default GoogleApiWrapper(
    (props) => ({
      apiKey: 'AIzaSyBgb4kpatKEjsOGsxplxFyRfw1K_wGhLTo',
      language: props.language,
    }
  ))(GoogleMap);
