import React from 'react';
import '../../css/AddDive.css';
import GoogleLogbookMap from './GoogleLogbookMap';
import AuthService from '../../util/AuthService';
import axios from 'axios';
import { withRouter } from 'react-router';
import swal from 'sweetalert';  
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import classnames from 'classnames';  
import { createLogbook } from '../../actions/logbookActions';        

class AddDive extends React.Component {
    constructor() {
        super();

        this.state = {
            partnerName: '',
            partnerSurname: '',
            marker: {},
            entryTime: '',
            exitTime: '',
            averageDepth: 0.0,
            maxDepth: 0.0,
            visibility: 0.0,
            waterTemperature: 0.0,
            airTemperature: 0.0,
            cylinderCapacity: '',
            divingSuit: 'NONE',
            waterType: 'NONE',
            waterEntryType: 'NONE',
            ballast: 0.0,
            glovesType: 'NONE',
            divingType: 'NONE',
            comment: '',
            errors: {}
        }
        this.Auth = new AuthService();

        this.onChange = this.onChange.bind(this);
        this.setMarker = this.setMarker.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }

    componentWillReceiveProps(nextProps) {
        if(nextProps.errors) {
            this.setState({ errors: nextProps.errors });
        }
    }

    onChange(e) {
        this.setState({ [e.target.name]: e.target.value });
    }

    setMarker(newMarker) {
        this.setState({ marker: newMarker });
    }

    onSubmit(e) {
        // try {
        //     e.preventDefault();
        //     let jwtToken = this.Auth.getToken();

        //     const logbookObject = {
        //         partnerName: this.state.partnerName,
        //         partnerSurname: this.state.partnerSurname,
        //         marker: this.state.marker,
        //         entryTime: this.state.entryTime,
        //         exitTime: this.state.exitTime,
        //         averageDepth: this.state.averageDepth,
        //         maxDepth: this.state.maxDepth,
        //         visibility: this.state.visibility,
        //         waterTemperature: this.state.waterTemperature,
        //         airTemperature: this.state.airTemperature,    
        //         cylinderCapacity: this.state.cylinderCapacity,
        //         divingSuit: this.state.divingSuit,
        //         waterType: this.state.waterType,
        //         waterEntryType: this.state.waterEntryType,
        //         ballast: this.state.ballast,
        //         glovesType: this.state.glovesType,
        //         divingType: this.state.divingType,
        //         comment: this.state.comment
        //     }

        //     axios({
        //         method: 'POST',
        //         url: `/add/logbook/${jwtToken}`,
        //         data: logbookObject,
        //         headers: {
        //             "Accept": "application/json",
        //             "Content-type": "application/json"
        //         }
        //     }).then(response => {
        //         console.log(response);
        //         if(this.state.errors != null) {
        //             this.props.history.push("/logbook");
        //         }
        //     })

        // } catch(error) {
        //     swal("Error", "Something goes wrong.", "error");
        // }
        e.preventDefault();
        let jwtToken = this.Auth.getToken();

        const logbookObject = {
            partnerName: this.state.partnerName,
            partnerSurname: this.state.partnerSurname,
            marker: this.state.marker,
            entryTime: this.state.entryTime,
            exitTime: this.state.exitTime,
            averageDepth: this.state.averageDepth,
            maxDepth: this.state.maxDepth,
            visibility: this.state.visibility,
            waterTemperature: this.state.waterTemperature,
            airTemperature: this.state.airTemperature,    
            cylinderCapacity: this.state.cylinderCapacity,
            divingSuit: this.state.divingSuit,
            waterType: this.state.waterType,
            waterEntryType: this.state.waterEntryType,
            ballast: this.state.ballast,
            glovesType: this.state.glovesType,
            divingType: this.state.divingType,
            comment: this.state.comment
        }
        this.props.createLogbook(logbookObject, jwtToken, this.props.history);
    }

    render() {
        const { errors } = this.state;

        return (
            <div className="add-dive-container">
                <div className="add-dive-title">
                    Add dive to logbook
                </div>
                <div className="add-dive-center">
                    <div className="add-dive-box dive-shadow">
                        <form onSubmit={this.onSubmit}>
                            <div className="form-group">
                                <label htmlFor="partnerName">Partner's name</label>
                                <input 
                                    type="text" 
                                    id="partnerName" 
                                    placeholder="Enter partner's name"
                                    name="partnerName"
                                    value={this.state.partnerName}
                                    onChange={this.onChange}
                                    className={classnames("form-control",  {
                                        "is-invalid": errors.partnerName
                                    })}
                                />
                                <div className="invalid-feedback">{errors.partnerName}</div>
                            </div>

                            <div className="form-group">
                                <label htmlFor="partnerSurname">Partner's surname</label>
                                <input 
                                    type="text" 
                                    id="partnerSurname" 
                                    placeholder="Enter partner's surname" 
                                    name="partnerSurname"
                                    value={this.state.partnerSurname}
                                    onChange={this.onChange}
                                    className={classnames("form-control",  {
                                        "is-invalid": errors.partnerSurname
                                    })}
                                />
                                <div className="invalid-feedback">{errors.partnerSurname}</div>
                            </div>

                            <div className="form-group row">
                                <label htmlFor="entryTime" className="col-sm-2 col-form-label">Entry time</label>
                                <div className="col-sm-10">
                                    <input 
                                        type="datetime-local" 
                                        className="form-control" 
                                        id="entryTime"
                                        name="entryTime"
                                        value={this.state.entryTime}
                                        onChange={this.onChange} 
                                    />
                                </div>
                            </div>

                            <div className="form-group row">
                                <label htmlFor="exitTime" className="col-sm-2 col-form-label">Exit time</label>
                                <div className="col-sm-10">
                                    <input 
                                        type="datetime-local" 
                                        className="form-control" 
                                        id="exitTime" 
                                        name="exitTime"
                                        value={this.state.exitTime}
                                        onChange={this.onChange}
                                    />
                                </div>
                            </div>

                            <div className="form-group">
                                <label htmlFor="avgDepth">Average depth [m]</label>
                                <input 
                                    type="number" 
                                    step="0.1" 
                                    min="1" 
                                    className="form-control" 
                                    id="avgDepth" 
                                    name="averageDepth"
                                    value={this.state.averageDepth}
                                    onChange={this.onChange}
                                />
                            </div>

                            <div className="form-group">
                                <label htmlFor="maxDepth">Maximum depth [m]</label>
                                <input 
                                    type="number" 
                                    step="0.1" min="1"
                                    className="form-control" 
                                    id="maxDepth" 
                                    name="maxDepth"
                                    value={this.state.maxDepth}
                                    onChange={this.onChange}
                                />
                            </div>

                            <div className="form-group">
                                <label htmlFor="visibility">Visibility [m]</label>
                                <input 
                                    type="number" 
                                    step="0.1" 
                                    min="0" 
                                    className="form-control" 
                                    id="visibility" 
                                    name="visibility"
                                    value={this.state.visibility}
                                    onChange={this.onChange}
                                />
                            </div>

                            <div className="form-group">
                                <label htmlFor="waterTemperature">Temperature of water [<sup>o</sup>C]</label>
                                <input 
                                    type="number" 
                                    step="0.1" 
                                    min="-5" 
                                    className="form-control" 
                                    id="waterTemperature" 
                                    name="waterTemperature"
                                    value={this.state.waterTemperature}
                                    onChange={this.onChange}
                                />
                            </div>

                            <div className="form-group">
                                <label htmlFor="airTemperature">Temperature of air [<sup>o</sup>C]</label>
                                <input 
                                    type="number" 
                                    step="0.1" 
                                    min="-100" 
                                    className="form-control" 
                                    id="airTemperature" 
                                    name="airTemperature"
                                    value={this.state.airTemperature}
                                    onChange={this.onChange}
                                />
                            </div>

                            <div className="form-row">
                                <div className="form-group col-md-5">
                                    <label htmlFor="cylinder">Cylinder capacity</label>
                                    <select 
                                        id="cylinder"
                                        name="cylinderCapacity"
                                        value={this.state.cylinderCapacity}
                                        onChange={this.onChange}
                                        className={classnames("custom-select mr-sm-2",  {
                                            "is-invalid": errors.cylinderCapacity
                                        })}
                                    >
                                        <option value="NONE">NONE</option>
                                        {/* <option defaultValue>Choose cylinder...</option> */}
                                        <option value="10L">10L</option>
                                        <option value="12L">12L</option>
                                        <option value="15L">15L</option>
                                        <option value="18L">18L</option>
                                        <option value="TWINSET">Twinset</option>
                                        <option value="REBREATHER">Rebreather</option>
                                    </select>
                                    <div className="invalid-feedback">{errors.cylinderCapacity}</div>
                                </div>

                                <div className="form-group col-md-3">
                                    <label htmlFor="suit">Suit</label>
                                    <select 
                                        id="suit"
                                        name="divingSuit"
                                        value={this.state.divingSuit}
                                        onChange={this.onChange}
                                        className={classnames("custom-select mr-sm-2",  {
                                            "is-invalid": errors.divingSuit
                                        })}
                                    >
                                        <option value="NONE">NONE</option>
                                        {/* <option defaultValue>Choose suit...</option> */}
                                        <option value="DRY">Dry suit</option>
                                        <option value="SEMIARID">Semiarid suit</option>
                                        <option value="WET">Wet suit</option>
                                    </select>
                                    <div className="invalid-feedback">{errors.divingSuit}</div>
                                </div>

                                <div className="form-group col-md-4">
                                    <label htmlFor="waterType">Water type</label>
                                    <select 
                                        id="waterType"
                                        name="waterType"
                                        value={this.state.waterType}
                                        onChange={this.onChange}
                                        className={classnames("custom-select mr-sm-2",  {
                                            "is-invalid": errors.waterType
                                        })}
                                    >
                                        <option value="NONE">NONE</option>
                                        {/* <option defaultValue>Choose water type...</option> */}
                                        <option value="SWEET">Sweet water</option>
                                        <option value="SALT">Salt water</option>
                                    </select>
                                    <div className="invalid-feedback">{errors.waterType}</div>
                                </div>
                            </div>

                            <div className="form-row">
                                <div className="form-group col-md-6">
                                    <label htmlFor="waterEntryType">Entry type to water</label>
                                    <select  
                                        id="waterEntryType"
                                        name="waterEntryType"
                                        value={this.state.waterEntryType}
                                        onChange={this.onChange}
                                        className={classnames("custom-select mr-sm-2",  {
                                            "is-invalid": errors.waterEntryType
                                        })}
                                    >
                                        <option value="NONE">NONE</option>
                                        {/* <option defaultValue>Choose entry type...</option> */}
                                        <option value="COAST">COAST</option>
                                        <option value="BOAT">BOAT</option>
                                    </select>
                                    <div className="invalid-feedback">{errors.waterEntryType}</div>
                                </div>

                                <div className="form-group col-md-6">
                                    <label htmlFor="glovesType">Gloves type</label>
                                    <select 
                                        id="glovesType"
                                        name="glovesType"
                                        value={this.state.glovesType}
                                        onChange={this.onChange}
                                        className={classnames("custom-select mr-sm-2",  {
                                            "is-invalid": errors.glovesType
                                        })}
                                    >
                                        <option value="NONE">NONE</option>
                                        {/* <option defaultValue>Choose gloves type...</option> */}
                                        <option value="WET">WET</option>
                                        <option value="DRY">DRY</option>
                                        <option defaultValue="NONE">NONE</option>
                                    </select>
                                    <div className="invalid-feedback">{errors.glovesType}</div>
                                </div>
                            </div>

                            <div className="form-group">
                                <label htmlFor="ballast">Ballast [kg]</label>
                                <input 
                                    type="number" 
                                    step="0.5" 
                                    min="0" 
                                    className="form-control" 
                                    id="ballast"
                                    name="ballast"
                                    value={this.state.ballast}
                                    onChange={this.onChange} 
                                />
                            </div>

                            <div className="form-group">
                                <label htmlFor="divingType">Diving type</label>
                                <select 
                                    id="divingType"
                                    name="divingType"
                                    value={this.state.divingType}
                                    onChange={this.onChange}
                                    className={classnames("custom-select mr-sm-2",  {
                                        "is-invalid": errors.divingType
                                    })}
                                >
                                    <option value="NONE">NONE</option>
                                    {/* <option defaultValue>Choose diving type...</option> */}
                                    <option value="RECREATIONAL">Recreational diving</option>
                                    <option value="TECHNICAL">Technical diving</option>
                                    <option value="CAVE">Cave diving</option>
                                    <option value="WRECK">Wreck diving</option>
                                </select>
                                <div className="invalid-feedback">{errors.divingType}</div>
                            </div>

                            <div className="form-group">
                                <label htmlFor="googleLogbookMap">Location</label>
                                <GoogleLogbookMap 
                                    setMarker={this.setMarker}
                                />
                            </div>

                            <div className="form-group">
                                <label htmlFor="comment">Comment</label>
                                <textarea 
                                    className="form-control" 
                                    id="comment" 
                                    rows="7"
                                    name="comment"
                                    value={this.state.comment}
                                    onChange={this.onChange}
                                >
                                </textarea>
                            </div>

                            <div className="btn-add-dive-center">
                                <button type="submit" className="btn btn-primary btn-lg btn-add-dive">
                                    Confirm
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        );
    }
}

AddDive.propTypes = {
    createLogbook: PropTypes.func.isRequired,
    errors: PropTypes.object.isRequired
}

const mapStateToProps = state => ({
    errors: state.errors
})

export default connect(mapStateToProps, { createLogbook })(withRouter(AddDive));
