import React from 'react';
import '../../css/TwitterCategoriesCard.css';
import $ from 'jquery';
import { Link } from 'react-router-dom'; 

class TwitterCategoriesCard extends React.Component {
    isMountedTwitterCategories = false;

    constructor(props) {
        super(props);

        this.onProfileClick = this.onProfileClick.bind(this);
        this.onRenderProfil = this.onRenderProfil.bind(this);
        this.onExploreClick = this.onExploreClick.bind(this);
    }

    componentDidMount() {
        this.isMountedTwitterCategories = true;
    }

    onProfileClick() {
        $(".tweets-likes-container").css({ "display": "none" });
        $(".twitter-user-profil").css({ "display": "block" })

    }

    onRenderProfil() {
        return this.isMountedTwitterCategories && 
            (
                <div className="twitter-user-profil">
                    <div className="twitter-header-profile">

                    </div>
                    <a class="twitter-timeline" href={`https://twitter.com/${this.props.screenName}?ref_src=twsrc%5Etfw`}>Tweets by {this.props.screenName}</a>
                </div>
            );
    }

    componentWillUnmount() {
        this.isMountedTwitterCategories = false;
    }

    onExploreClick() {
        this.props.history.push("/twitter/explore");
    }

    render() {
        return (
            <div className="twitter-categories-container">
                <ul className="list-group twitter-categories-list">
                    <li className="list-group-item list-group-item-hover">

                        <div className="twitter-categories-icons-box">
                            <i className="fas fa-home"></i> Home
                        </div>
                    </li>
                    {/* <Link to="/twitter/explore" className="list-group-item list-group-item-hover"> */}
                    <li onClick={this.onExploreClick} className="list-group-item list-group-item-hover">
                        <div className="twitter-categories-icons-box">
                            <span style={{ fontWeight: '900' }}>#</span> Explore
                        </div>
                    {/* </Link> */}
                    </li>
                    <li className="list-group-item list-group-item-hover">
                        <div className="twitter-categories-icons-box">
                            <i className="fas fa-clock"></i> Timeline likes
                        </div>
                    </li>
                    <li className="list-group-item list-group-item-hover">
                        <div className="twitter-categories-icons-box">
                            <i className="fas fa-bell"></i> Notifications
                        </div>
                    </li>
                    <li className="list-group-item list-group-item-hover">
                        <div className="twitter-categories-icons-box">
                            <i className="far fa-envelope"></i> Messages
                        </div>
                    </li>
                    <li 
                        className="list-group-item list-group-item-hover"
                        onClick={this.onProfileClick}
                    >
                        <div className="twitter-categories-icons-box">
                            <img 
                            src={this.props.pictureUrl} 
                            alt="Avatar" 
                            className="twitter-categories-avatar"
                            /> Profile
                        </div>
                    </li>
                    <li className="list-group-item list-group-item-hover add-tweet-button">
                        { this.isMountedTwitterCategories &&
                            <a 
                                className="twitter-share-button"
                                href="https://twitter.com/intent/tweet"
                                data-size="large"
                            >
                                Tweet
                            </a>
                        }
                    </li>
                </ul>
            </div>
        );
    }
}

export default TwitterCategoriesCard;
