import React from 'react';
import '../../css/AddTopic.css';
import $ from 'jquery';
import Unsplash from 'unsplash-js';
import { Redirect } from 'react-router-dom';

class AddTopic extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            title: '',
            message: '',
            isInvalidTitle: false,
            isInvalidMessage: false
        }
        this.errors = [];
        this.fileInput = React.createRef();

        this.onChange = this.onChange.bind(this);
        this.onSubmitForm = this.onSubmitForm.bind(this);
        this.validateForm = this.validateForm.bind(this);
        this.onClick = this.onClick.bind(this);

        this.unsplash = new Unsplash({
            applicationId: "a7dc421f598ef954b00d0880bb3ede81f8973bd21c43be006b6be14ca8f100d9",
            secret: "957ca7dde447819e29723f96628960eb8668a7a00be0d60d39f4e4a1ef38b0d3",
        });
    }

    onChange(e) {
        this.setState({ [e.target.name]: e.target.value });
    }

    showInvalidTitle(isInvalidTitle) {
        if(isInvalidTitle) {
            return (
                <div className="alert alert-danger">
                    Title length must be between 10 and 160 characters.
                </div>
            );
        }
        return null;
    }

    showInvalidMessage(isInvalidMessage) {
        if(isInvalidMessage) {
            return (
                <div className="alert alert-danger">
                    Message length must be minimum 10 characters.
                </div>
            );
        }
        return null;
    }

    validateForm(e) {
        if(this.state.title.length < 10 || this.state.title.length > 160) {
            e.preventDefault();
            this.setState({ isInvalidTitle: true });
            this.errors.push(true);
        }

        if(this.state.message.length < 10) {
            e.preventDefault();
            this.setState({ isInvalidMessage: true });
            this.errors.push(true);
        }
    }

    onClick() {
        // const unsplash = new Unsplash({
        //     applicationId: "a7dc421f598ef954b00d0880bb3ede81f8973bd21c43be006b6be14ca8f100d9",
        //     secret: "957ca7dde447819e29723f96628960eb8668a7a00be0d60d39f4e4a1ef38b0d3",
        // });

        const authenticationUrl = this.unsplash.auth.getAuthenticationUrl([
            "public", 
            "read_user",
            "write_user",
            "read_photos",
            "write_photos"
        ]);
        console.log(authenticationUrl);
        let redirectIndex = authenticationUrl.indexOf("redirect_uri");

        let beforeAUthenticationUrl = authenticationUrl.substr(0, redirectIndex + 1 + "redirect_uri".length);
        console.log(beforeAUthenticationUrl);

        let responseTypeIndex = authenticationUrl.indexOf("response_type");
        let result = beforeAUthenticationUrl + "urn:ietf:wg:oauth:2.0:oob" + authenticationUrl.substr(responseTypeIndex - 1, authenticationUrl.length - responseTypeIndex + 1);
        console.log(result);

        // window.location.assign(result);

        return <Redirect to={result} />
    }

    onSubmitForm(e) {
        e.preventDefault();

        this.validateForm(e);

        if(this.errors.length === 0) {
            // console.log(this.fileInput.current.files[0].name);

            this.unsplash.auth.userAuthentication("")
            .then(toJson => toJson.json())
            .then(json => {
                console.log(json);
                unsplash.auth.setBearerToken(json.access_token);
            });
        }
    }

    render() {
        return (
            <div className="add-topic-container add-topic-center">
                <div className="new-topic-box new-topic-box-shadow">
                    <div className="new-topic-title">
                        Add new topic
                    </div>

                    <form onSubmit={this.onSubmitForm}>
                        <div className="form-group">
                            <label for="title">Title</label>
                            <input 
                                className="form-control form-control-lg"
                                type="text"
                                id="title"
                                name="title"
                                placeholder="Enter title"
                                value={this.state.title}
                                onChange={this.onChange}
                            />
                        </div>
                        { this.showInvalidTitle(this.state.isInvalidTitle) }

                        <div className="form-group">
                            <label for="message">Message</label>
                            <textarea
                                className="form-control form-control-lg"
                                id="message"
                                name="message"
                                value={this.state.message}
                                onChange={this.onChange}
                                rows="17"
                            >
                            </textarea>
                        </div>
                        { this.showInvalidMessage(this.state.isInvalidMessage) }

                        <div className="form-group">
                            <label for="file">Upload data</label>
                            <input 
                                id="file"
                                type="file"
                                className="form-control-file"
                                ref={this.fileInput}
                            />
                        </div>

                        <button type="submit" onClick={this.onClick} className="btn btn-primary new-topic-btn">
                            Publicate
                        </button>
                    </form>
                </div>
            </div>
        );
    }
}

export default AddTopic;
