import React from 'react';
import '../../css/Forum.css';
import poland from '../../img/flags/poland.jpg';
import germany from '../../img/flags/germany.png';
import england from '../../img/flags/england.jpg';

class Forum extends React.Component {
    constructor(props) {
        super(props);

        this.isSelected = false;
        this.onFlagClick = this.onFlagClick.bind(this);
    }

    onFlagClick() {
        $(".flag-item img").click(function() {
            if(!this.isSelected) {
                this.isSelected = true;
                $(this).addClass("isActiveFlag");
            } else {
                this.isSelected = false;
                $(this).removeClass("isActiveFlag");
            }
        });
    }

    render() {
        return (
            <div className="forum-container">
                <div className="forum-title">
                    Select forum
                </div>
                <div className="language-forum-box language-forum-box-center language-shadow">
                    <div className="flag-item">
                        <img 
                            src={poland} 
                            alt="Polish flag"
                            onClick={this.onFlagClick}
                        />
                    </div>
                    <div className="flag-item">
                        <img 
                            src={germany} 
                            alt="Germany flag" 
                            onClick={this.onFlagClick}
                        />
                    </div>
                    <div className="flag-item">
                        <img 
                            src={england} 
                            alt="English flag"
                            onClick={this.onFlagClick} 
                        />
                    </div>
                </div>
            </div>
        );
    }
}

export default Forum;
