import React from 'react';
import '../../css/Forum.css';
import poland from '../../img/flags/poland.jpg';
import germany from '../../img/flags/germany.png';
import england from '../../img/flags/england.jpg';
import $ from 'jquery';

class Forum extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            selectedForum: ''
        }
        this.onFlagClick = this.onFlagClick.bind(this);
    }

    onFlagClick() {
        $("#polandFlag").click(function() {
            this.setState({ selectedForum: 'POLISH' });

            $(this).addClass("isActiveFlag");
            $("#germanyFlag").removeClass("isActiveFlag");
            $("#englandFlag").removeClass("isActiveFlag");
        });

        $("#germanyFlag").click(function() {
            this.setState({ selectedForum: 'GERMANY' });

            $(this).addClass("isActiveFlag");
            $("#polandFlag").removeClass("isActiveFlag");
            $("#englandFlag").removeClass("isActiveFlag");
        });

        $("#englandFlag").click(function() {
            this.setState({ selectedForum: 'ENGLISH' });

            $(this).addClass("isActiveFlag");
            $("#germanyFlag").removeClass("isActiveFlag");
            $("#polandFlag").removeClass("isActiveFlag");
        });
    }

    render() {
        return (
            <div className="forum-container">
                <div className="forum-title">
                    Select forum
                </div>
                <div className="language-forum-box language-forum-box-center language-shadow">
                    <div className="flag-item">
                        <img 
                            id="polandFlag"
                            src={poland} 
                            alt="Polish flag"
                            onClick={this.onFlagClick}
                        />
                    </div>
                    <div className="flag-item">
                        <img 
                            id="germanyFlag"
                            src={germany} 
                            alt="Germany flag" 
                            onClick={this.onFlagClick}
                        />
                    </div>
                    <div className="flag-item">
                        <img 
                            id="englandFlag"
                            src={england} 
                            alt="English flag"
                            onClick={this.onFlagClick} 
                        />
                    </div>
                </div>
                <Link to={`/create/topic/${this.state.selectedForum}`}>
                    <button className="btn btn-primary">
                        CREATE TOPIC
                    </button>
                </Link>
            </div>
        );
    }
}

export default Forum;
