import React from 'react';
import '../../../css/twitter-home/TwitterHomeAdd.css';
import * as filestack from 'filestack-js';
import ReactFilestack from 'filestack-react';
import $ from 'jquery';

class TwitterHomeAdd extends React.Component {
    constructor() {
        super();

        this.state = {
            files: []
        }
        this.removeRenderedFile = this.removeRenderedFile.bind(this);
    }

    componentDidMount() {

    }

    removeRenderedFile(handle) {
        console.log(handle);
    }

    renderFiles() {
        $("tweet-add-uploaded-files").html("");
        return this.state.files.map((file, index) => {
            if(file.mimetype.contains("image")) {
                return (
                    <React.Fragment>
                        <img
                            className="tweet-add-rendered-file-image"
                            src={file.url}
                            alt={file.filename}
                        />
                        <br />
                        <button className="upload-file-delete" onClick={() => this.removeRenderedFile(file.handle)}>Delete - {file.filename}</button>
                    </React.Fragment>
                );
            }
        });
    }

    callback(response) {
        console.log(response);
    }

    uploadMedia() {
        // const clientOptions = {
        //     security: {
        //         policy: "eyJjYWxsIjpbInBpY2siLCJzdG9yZSIsInJ1bldvcmtmbG93Il0sImV4cGlyeSI6MTg5MzQ1MDYwMH0=",
        //         signature: "8c38c1201c5d885c5f52451c4ed42d7af19bc95ae90972746d804cfe46c58f7b"
        //     }
        // }
        const client = filestack.init("Abn3RoxlVQeWNtMpk2Gflz");
        // const options = {
        //     onFileUploadFinished: (response) => {
        //         this.forceUpdate();
        //         this.setState({ files: this.state.files.concat(response) }, () => {
        //             console.log(this.state.files);
        //             this.forceUpdate();
        //         });
        //         console.log(response);

        //     },
        //     maxSize: 5 * 1024 * 1024, // 5MB
        //     uploadInBackground: false,
        //     maxFiles: 4
        // }
        client.picker({}).open();
    }

    render() {
        let renderedFiles = "";
        if(this.state.files.length > 0) {
            renderedFiles = this.renderFiles();
        }
        console.log(renderedFiles);

        return (
            <div className="add-tweet-container">
                <div className="avatar-tweet-textarea-container">
                    <div className="tweet-add-avatar-container">
                        <img 
                            src={this.props.pictureUrl} 
                            alt="Avatar" 
                            className="tweet-add-avatar"
                        />
                    </div>
                    <div className="tweet-add-textarea-container">
                        <textarea>
                            What's happening?
                        </textarea>


                    </div>
                </div>
                <div className="tweet-add-uploaded-files">
                    { renderedFiles }
                </div>
                <div className="add-tweet-buttons">
                    <ReactFilestack
                        apikey="Abn3RoxlVQeWNtMpk2Gflz"
                        action="pick"
                        actionOptions={{
                            maxSize: 5 * 1024 * 1024,
                            uploadInBackground: false,
                            maxFiles: 4
                        }}
                        customRender={({}) => {
                            <i 
                                className="far fa-images" 
                                style={{ color: '#00A4EF', fontSize: "1.5vw", width: "15%", marginTop: "3%"}}
                            ></i>
                        }}
                        onSuccess={this.callback}
                    />
                    <button 
                        className="add-tweet-button-home-timeline"
                    >
                        Tweet
                    </button>
                </div>
            </div>
        );
    }
}

export default TwitterHomeAdd;
