import React from 'react';
import '../../../css/twitter-messages/SingleMessage.css';
import $ from 'jquery';
class SingleMessage extends React.Component {

    componentDidMount() {
        if(this.props.senderId === this.props.twitterOwnerId) {
            $(`.twitter-single-message-wrapper:eq(${this.props.index})`).css({ "justify-content": "flex-end", "background-color": "red" });
        } else {
            $(`.twitter-single-message-wrapper:eq(${this.props.index})`).css({ "justify-content": "flex-start", "background-color": "blue" });
        }
        let wrapper = $(`.twitter-single-message-wrapper:eq(${this.props.index})`);
        console.log(wrapper);
    }
    render() {
        return (
            <div className="twitter-single-message-wrapper">
                <div className="twitter-single-message-container">
                    <div className="twitter-single-message-text">
                        { this.props.text }
                    </div>
                        { this.props.createdAt }
                </div>
            </div>
        );
    }
}

export default SingleMessage;
