import React from 'react';
import '../../css/Header.css';
import { Link } from 'react-router-dom';
import { withTranslation } from 'react-i18next';
import FB from 'fb';
import axios from 'axios';
import { isAuthenticated, accessToken } from '../../context/AuthenticatedContext';

class Header extends React.Component {
    constructor() {
        super();

        this.state = {
            isAuthenticated: false
        }
    }

    componentDidMount() {
        // window.fbAsyncInit = function() {
        //     FB.init({
        //       appId      : '455695445269575',
        //       cookie     : true,
        //       xfbml      : true, 
        //       version    : 'v2.1'
        //     });

        //     FB.getLoginStatus(function(response) {
        //         FB.api('/me', 'GET', {
        //             fields: 'id,name,email', 
        //             access_token: accessToken
        //         }, function(response) {
        //             console.log(response);
        //         });
        //     }.bind(this));

        // }.bind(this);

        // (function(d, s, id) {
        //     var js, fjs = d.getElementsByTagName(s)[0];
        //     if (d.getElementById(id)) return;                js = d.createElement(s); js.id = id;
        //     js.src = "//connect.facebook.net/en_US/all.js";
        //     fjs.parentNode.insertBefore(js, fjs);
        // }(document, 'script', 'fb-root'));


        FB.api('/me', 'GET', {
            fields: 'id,name,email', 
            access_token: accessToken
        }, function(response) {
            console.log(response)
        });


        // axios({
        //     method: 'GET',
        //     url: '/isAuthenticated',
        //     data: '',
        //     headers: {
        //         "Accept": "application/json",
        //         "Content-type": "application/json"
        //     },
        // }).then(function (response) {
        //     console.log(response);
        // })
    }

    render() {
        const  { i18n } = this.props;

        const loggedIn = (
            <Link to="/logout" className="nav-link">
                Logout
            </Link>
        );

        const notLoggedIn = (
            <Link to="/login" className="nav-link">
                Log In
            </Link>
        );

        let logout;
        if(isAuthenticated) {
            logout = loggedIn;
        } else {
            logout = notLoggedIn;
        }
        return (
            <header>
                <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
                    <Link to="/home" className="navbar-brand">
                        <span className="logoSize">
                            Divelog
                        </span>
                    </Link>
                    <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarToggleExternalContent" aria-controls="navbarToggleExternalContent" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarToggleExternalContent">
                        <ul className="navbar-nav mr-auto">
                            <li className="nav-item mt-2">
                                <i
                                    onClick={() => i18n.changeLanguage("en")} 
                                    className="gb uk flag nav-link"
                                />
                            </li>
                            <li className="nav-item mt-2">
                                <i 
                                    onClick={() => i18n.changeLanguage("de")} 
                                    className="de flag nav-link"
                                />
                            </li>
                            <li className="nav-item mt-2">
                                <i 
                                    onClick={() => i18n.changeLanguage("pl")} 
                                    className="pl flag nav-link" 
                                />
                            </li>
                            <li className="nav-item">
                                <Link to="/home" className="nav-link">
                                    {this.props.t("header.home")}
                                </Link>
                            </li>
                            <li className="nav-item">
                                <Link to="/guide" className="nav-link">
                                    {this.props.t("header.guide")}
                                </Link>
                            </li>
                            <li className="nav-item">
                                <Link to="/about" className="nav-link">
                                    {this.props.t("header.aboutMe")}
                                </Link>
                            </li>
                            <li className="nav-item">
                                <Link to="/donate" className="nav-link">
                                    {this.props.t("header.donate")}
                                </Link>
                            </li>
                            <li className="nav-item">
                                <Link to="/contact" className="nav-link">
                                    {this.props.t("header.contact")}
                                </Link>
                            </li>
                        </ul>
                        <ul className="navbar-nav my-lg justify-content-end">
                            <li className="nav-item">
                                { logout }
                            </li>
                        </ul>
                    </div>
                </nav>
            </header>
        );
    }
}

export default withTranslation('common')(Header);
