import React from 'react';
import { Redirect } from 'react-router-dom';

export default function withTwitterAuth(AuthComponent, props) {
    class AuthWrapped extends React.Component {
        constructor() {
            super();

            this.state = {
                isRedirectToLoginPage: true
            }
        }
        componentWillMount() {
            fetch(`/twitter/login/validate/token/${this.props.match.params.jwtToken}`, {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json'
                }
            }).then(response => {
                if(response.status === 200) {
                    this.setState({ isRedirectToLoginPage: false }, () => {
                        localStorage.setItem("twitterJwtToken", this.props.match.params.jwtToken);
                    });
                } else {
                    this.props.history.replace("/login");
                }
            });
        }

        // history={`/twitter/${this.props.match.params.jwtToken}`}

        render() {
            let isRedirectToLoginPage = this.state.isRedirectToLoginPage;

            return (
                 !isRedirectToLoginPage && <AuthComponent />
            );
        }
    }
    return AuthWrapped;
}
