    }

    retrieveContacts() {
        let jwtToken = this.Auth.getRightSocialToken();

        axios({
            url: `/twitter/direct/messages/search/people/${jwtToken}`,
            method: 'POST',
            data: this.state.searchInput,
            headers: {
                'content-type': 'application/json'
            }
        }).then(response => {
            console.log(response.data);
            response.data.map((contact, index) => {
                const element = {
                    isContactPossible: contact.dmaccessible,
                    name: contact.name,
                    screenName: contact.screenName,
                    pictureUrl: contact.pictureUrl
                }
                this.setState({ contacts: this.state.contacts.concat(element) });
            });
            this.setState({ 
                isContactsRetrieved: true,
                isLoading: false
            });
        });
    }

    renderContacts() {
        return this.state.contacts.map((contact, index) => {
            return (
                <ContactModal 
                    key={index}
