                        // json.map((tweetHtml, index) => {
                        //     this.setState({ tweets: this.state.tweets.concat(tweetHtml) });
                        // });
                        // this.setState({ isTweetsRetrieved: true });


                            window.twttr.ready(function(twttr) {
                                console.log(twttr);
                                let content = "";

                                json.map((tweet, index) => {
                                    twttr.widgets.createTweet(
                                        tweet.id,
                                        document.getElementById('home-tweets-container'),
                                        {
                                            theme: 'dark'
                                        }
                                    ).then(function(response) {
                                        console.log(response);
                                        content += response;
                                    });
                                });
                                $(".tweets-likes-container").html(content);
                            });
                            // window.twttr.events.bind(
                            //     'loaded',
                            //     function(twttr) {
                            //         console.log(twttr);
                            // });
                    });
                });
            }
        }); 
    }

    renderTweets() {
        // let content = "";
        // this.state.tweets.map((tweetHtml, index) => {
        //     content += tweetHtml;
        // });
        // $(".tweets-likes-container").html(content);
        // $(".twitter-tweet-rendered").css({ "width": "100%" });

        this.state.tweets.map((id, index) => {

        });



    }

    componentWillUnmount() {
        this.isMountedTwitter = false;
    }

    render() {
        let isTweetsRetrieved = this.state.isTweetsRetrieved;

        return (
            <div className="twitter-container">
                <div className="twitter-grid-container">
                    <div className="twitter-grid-item-1">
                        <div className="twitter-left-categories-container">
                            <div className="twitter-profil-container">
                                { this.state.name }
                            </div>
                            <TwitterCategoriesCard
                                pictureUrl={this.state.pictureUrl}
                                screenName={this.state.screenName}
                            />    
                        </div>
                    </div>
                    <div className="feed-container">
                        <div id="home-tweets-container" className="tweets-likes-container">
                            { isTweetsRetrieved && this.renderTweets() }
                        </div>
                    </div>
                    <div className="twitter-grid-item-3">
                        <div className="twitter-rl-container">
                            <div className="twitter-groups-container">
                                <SearchTwitterPeople />
                                <AvailableTrends />
                            </div>
                        </div>
                        <div className="twitter-rr-container">
                            <div className="twitter-friends-container">
                                {/* <TwitterFriendsList /> */}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withAuth(TwitterHome, { twitterHome: true });
