import React from 'react';
import '../../css/AddTopic.css';
import $ from 'jquery';

class AddTopic extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            title: '',
            message: ''
        }
        this.onChange = this.onChange.bind(this);
    }

    componentDidMount() {
        $("nav").css({ "position": "fixed", "width": "100%" });
        $(".new-topic.box").css({ "margin-top": "10%" });
    }

    onChange(e) {
        this.setState({ [e.target.name]: e.target.value });
    }

    render() {
        return (
            <div className="add-topic-container add-topic-center">
                <div className="new-topic-box new-topic-box-shadow">
                    <div className="new-topic-title">
                        Add new topic
                    </div>

                    <form>
                        <div className="form-group">
                            <label for="title">Title</label>
                            <input 
                                className="form-control form-control-lg"
                                type="text"
                                id="title"
                                name="title"
                                placeholder="Enter title"
                                value={this.state.title}
                                onChange={this.onChange}
                            />
                        </div>
                        <div className="form-group">
                            <label for="message">Message</label>
                            <textarea
                                className="form-control form-control-lg"
                                id="message"
                                name="message"
                                value={this.state.message}
                                onChange={this.onChange}
                                rows="17"
                            >
                            </textarea>
                        </div>
                        <button type="submit" className="btn btn-primary new-topic-btn">
                            Publicate
                        </button>
                    </form>
                </div>
            </div>
        );
    }
}

export default AddTopic;
