import React from 'react';

class Conversation extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            isLoadingConversation: this.props.isLoadingConversation
        }
    }

    componentDidMount() {

    }

    render() {
        let isLoadingConversation = this.state.isLoadingConversation;
        return (
            <React.Fragment>
                { !isLoadingConversation &&
                    <div className="twitter-messages-person-invite">
                        <span style={{ fontWeight: 700, fontSize: '1.1vw' }}>You don't have a message selected</span>
                        <br />
                        Choose one from your existing messages, or start a new one.
                        <br />
                        <button 
                            className="twitter-message-person-btn-new-message"
                            onClick={this.searchPeopleToConversation}
                        >
                            New message
                        </button>
                    </div>
                }
                { isLoadingConversation &&
                    <div 
                        className='spinner-border text-primary' 
                        role='status'
                    >
                        <span class='sr-only'>
                            Loading...
                        </span>
                    </div>
                }
            </React.Fragment>
        );
    }
}

export default Conversation;
