import React from 'react';
import '../../css/TimelineLikes.css';
import withAuth from '../../util/withAuth';
import AuthService from '../../util/AuthService';
import TwitterCategoriesCard from './../twitter/TwitterCategoriesCard';
import SearchTwitterPeople from './../twitter/SearchTwitterPeople';
import AvailableTrends from './../twitter/AvailableTrends';
import TwitterFriendsList from './../twitter/TwitterFriendsList';
import $ from 'jquery';

class TwitterHome extends React.Component {
    isMountedTwitter = false;
    constructor(props) {
        super(props);

        this.state = {
            accessToken: '',
            email: '',
            name: '',
            userID: '',
            pictureUrl: '',
            providerId: '',
            screenName: '',
            tokenSecret: '',
            tweets: [],
            isTweetsRetrieved: false
        }
        this.tweetIds = [];
        this.Auth = new AuthService();
        this.renderTweets = this.renderTweets.bind(this);
    }

    componentWillMount() {
        window.twttr.widgets.load();
    }

    componentDidMount() {
        this.isMountedTwitter = true;

        let jwtToken = this.Auth.getRightSocialToken();

        fetch(`/getuserdata/${jwtToken}`, {
            method: 'GET',
            headers: {
              'content-type': 'application/json'
            }
        })
        .then(response => response.json())
        .then(jsonData => {
            if(this.isMountedTwitter) {
                this.setState({
                    accessToken: jsonData.accessToken,
                    email: jsonData.email,
                    name: jsonData.name,
                    twitterUserID: jsonData.twitterUserID,
                    pictureUrl: jsonData.pictureUrl,
                    providerId: jsonData.providerId,
                    screenName: jsonData.screenName,
                    tokenSecret: jsonData.tokenSecret
                }, () => {
                    fetch(`/twitter/home/timeline/${jwtToken}`, {
                        method: 'GET',
                        headers: {
                            'content-type': 'application/json'
                        }
                    }).then(response => response.json())
                    .then(json => {
                        json.map((tweet, index) => {
                            // this.tweetIds.push(tweet.id);
                            this.setState({ tweets: this.state.tweets.concat(tweet.id) });
                        });
                    });
                });
                this.setState({ isTweetsRetrieved: true });
            }

        });

    }

    renderTweets() {
        // let content = "";
        window.twttr.ready(function(twttr) {
            console.log(twttr);
            this.state.tweets.map((id, index) => {
                console.log(id);
                twttr.widgets.createTweet(
                    id,
                    document.getElementById("home-tweets-container")
                ).then(function(response) {
                    console.log(response);
                });
            });
            // $(".tweets-likes-container").html(content);
        });

    }

    componentWillUnmount() {
        this.isMountedTwitter = false;
    }

    render() {
        let isTweetsRetrieved = this.state.isTweetsRetrieved;

        return (
            <div className="twitter-container">
                <div className="twitter-grid-container">
                    <div className="twitter-grid-item-1">
                        <div className="twitter-left-categories-container">
                            <div className="twitter-profil-container">
                                { this.state.name }
                            </div>
                            <TwitterCategoriesCard
                                pictureUrl={this.state.pictureUrl}
                                screenName={this.state.screenName}
                            />    
                        </div>
                    </div>
                    <div className="feed-container">
                        <div id="home-tweets-container" className="tweets-likes-container">
                            { isTweetsRetrieved && this.renderTweets() }
                        </div>
                    </div>
                    <div className="twitter-grid-item-3">
                        <div className="twitter-rl-container">
                            <div className="twitter-groups-container">
                                <SearchTwitterPeople />
                                <AvailableTrends />
                            </div>
                        </div>
                        <div className="twitter-rr-container">
                            <div className="twitter-friends-container">
                                {/* <TwitterFriendsList /> */}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default withAuth(TwitterHome, { twitterHome: true });
