import React from 'react';
import { withRouter } from 'react-router-dom';
import FB from 'fb';

class User extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            accessToken: '',
            email: '',
            name: '',
            userID: ''
        }
        this.onSubmit = this.onSubmit.bind(this);
    }

    componentDidMount() {
        getUserData(this.state);
    }

    onSubmit() {
        window.location.href = "https://www.facebook.com/logout.php?next=http://localhost:3000/&access_token=" + this.state.accessToken;
    }

    render() {
        return (
            <div>
                Codecool
                <br />
                <br />
                <button onClick={this.onSubmit}>Logout</button>
            </div>
        );
    }
}

function getUserData(state) {
    let userID = localStorage.getItem("KEY");

    fetch(`/getuserdata/${userID}`, {
        method: 'GET',
        headers: {
            'content-type': 'application/json'
        }
    })
    .then(response => response.json())
    .then(jsonData => {
        this.setState(state => ({
            accessToken: jsonData.accessToken,
            email: jsonData.email,
            name: jsonData.name,
            userID: jsonData.userID
        }));
    }); 
    localStorage.removeItem("KEY");
}

export default withRouter(User);
