import React from 'react';
import FB from 'fb';
import axios from 'axios';
import { withRouter } from 'react-router-dom';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { getUserData } from '../actions/LoginActions';

class User extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            userData: {}
        }
        // this.loadUserData = this.loadUserData.bind(this);
    }
    componentDidMount() {
        let userID = localStorage.getItem("KEY");
        // this.props.getUserData(userID);
        // fetch(`/getuserdata/${userID}`)
        // .then(response => {
        //     console.log("fetch response");
        //     console.log(response);
        // }) 

        fetch(`/getuserdata/${userID}`, {
            method: 'GET',
            headers: {
              'content-type': 'application/json'
            }
          })
          .then(response => response.json())
          .then(jsonData => {
            console.log(jsonData.accessToken);
          })

        // this.loadUserData();


        // FB.api('/me', { fields: 'id,name,email', access_token: this.props.user.accessToken }, function (response2) {
        //     console.log("response 2: ");
        //     console.log(response2);
        // });

        window.fbAsyncInit = function() {
            FB.init({
                appId      : '455695445269575',
                cookie     : true,
                xfbml      : true, 
                version    : 'v3.3'
            });

            FB.getLoginStatus(function(response) {
                // FB.api('/me', 'GET', {
                //     fields: 'id,name,email', 
                //     access_token: accessToken
                // }, function(response) {
                //     console.log(response);
                // });
                console.log(response);
            }.bind(this));

        }.bind(this);

        (function(d, s, id) {
            var js, fjs = d.getElementsByTagName(s)[0];
            if (d.getElementById(id)) return;                js = d.createElement(s); js.id = id;
            js.src = "//connect.facebook.net/en_US/all.js";
            fjs.parentNode.insertBefore(js, fjs);
        }(document, 'script', 'facebook-jssdk'));
    }

    // loadUserData() {
    //     const { user } = this.props.user;
    //     console.log("user: ");
    //     console.log(user);
    //     this.setState({ userData: user});
    // }

    render() {
        console.log(this.props.user);
        return (
            <div>
                Codecool
            </div>
        );
    }
}

User.propTypes = {
    user: PropTypes.object.isRequired,
    getUserData: PropTypes.func.isRequired
}

const mapStateToProps = state => ({
    user: state.user
})

export default connect(mapStateToProps, { getUserData })(withRouter(User));
