import React from 'react';
import { Redirect } from 'react-router-dom';

export default function withTwitterAuth(AuthComponent, props) {
    class AuthWrapped extends React.Component {
        constructor() {
            super();

            this.state = {
                isRedirectToLoginPage: true
            }
        }
        componentWillMount() {
            fetch(`/twitter/login/validate/token/${jwtToken}`, {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json'
                }
            }).then(response => {
                console.log(response);
                if(response.status === 200) {
                    this.setState({ isRedirectToLoginPage: false }, () => {
                        localStorage.setItem("twitterJwtToken", this.props.match.params.jwtToken);
                    });
                }
            });
        }

        render() {
            let isRedirectToLoginPage = this.state.isRedirectToLoginPage;

            if(!isRedirectToLoginPage) {
                return <AuthComponent history={this.props.history} />
            }
            return <Redirect to="/login" />
        }
    }
    return AuthWrapped;
}
