import React from 'react';
import '../css/LogIn.css';
import logo from '../img/logo.png';
import { withTranslation } from 'react-i18next';
import FacebookLogin from 'react-facebook-login';
import axios from 'axios';
import { Redirect } from 'react-router-dom';
import { AuthObject } from '../util/AuthObject';
import AuthService from '../util/AuthService';
import $ from 'jquery';
import TwitterLogin from 'react-twitter-auth';

class LogIn extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            redirectToReferrer: false
        }
        this.Auth = new AuthService();
        this.responseFacebook = this.responseFacebook.bind(this);
        this.onTwitterClick = this.onTwitterClick.bind(this);
        this.onFailed = this.onFailed.bind(this);
        this.onSuccess = this.onSuccess.bind(this);
    }

    componentDidMount() {
        $("nav").css({ "position": "fixed", "width": "100%"});
    }

    componentWillUnmount() {
        $("nav").css({ "position": "static"});
    }

    responseFacebook(response) {
        const loginRequest = {
            accessToken: response["accessToken"],
            email: response["email"],
            name: response["name"],
            userID: response["userID"],
            pictureUrl: response["picture"]["data"]["url"]
        }

        axios({
            method: 'POST',
            url: '/signin',
            data: loginRequest,
            headers: {
                "Accept": "application/json",
                "Content-type": "application/json"
            }
        }).then(response => {
            this.Auth.setToken(response["data"]);
        });

        AuthObject.authenticate(() => {
            this.setState(() => ({redirectToReferrer: true }));
        })
        AuthObject.setPrincipal(loginRequest);

        return <Redirect to="/dashboard/" />
    }

    onFailed(response) {
        console.log(response);
    }

    onSuccess(response) {
        console.log(response);
        response.json().then(body => {
            alert(JSON.stringify(body));
          });
    }

    onTwitterClick() {
        window.location.href = "http://divelog.us-east-1.elasticbeanstalk.com/signin/twitter";
    }

    render() {
        const { from } = this.props.location.state || { from: { pathname: '/dashboard' }}
        const { redirectToReferrer } = this.state;

        if(redirectToReferrer === true) {
            return <Redirect to={from} />
        }

        return(
            <div className="login-container">
                <div className="login-form-container form-container-shadow form-size">
                    <div>
                        <span className="logo-text">
                            {this.props.t("login.login")}
                        </span>
                        <br />
                        <img src={logo} alt="divelog" className="logo-space logo" />
                        <br />
                        <span className="logo-text">Divelog</span>
                        <br />
                        Hubert Strumi≈Ñski
                        <br />
                        <FacebookLogin
                            appId="455695445269575"
                            autoLoad={false}
                            fields="name,email,picture"
                            scope="public_profile,user_friends"
                            callback={this.responseFacebook}
                            cssClass="btn btn-lg btn-fb fb-btn-white-font"
                            icon="<i className='fab fa-facebook-f pr-1' />"
                            textButton={this.props.t("login.facebook-btn")}
                            installed={true}
                        />
                        <br />
                        <button 
                            type="button" 
                            className="btn btn-tw fb-btn-white-font btn-tw-background btn-tw-width"
                            onClick={this.onTwitterClick}
                        >
                            <i class="fab fa-twitter pr-1"></i> 
                            LOGIN WITH TWITTER
                        </button>
                        <TwitterLogin 
                            // loginUrl="http://divelog.com.s3-website-us-east-1.amazonaws.com/signin/twitter"
                            loginUrl="http://127.0.0.1/signin/twitter"
                            onFailure={this.onFailed}
                            onSuccess={this.onSuccess}
                            // requestTokenUrl="http://divelog.com.s3-website-us-east-1.amazonaws.com/callback"
                            requestTokenUrl="http://127.0.0.1/callback"
                            customHeaders={{
                                'Access-Control-Allow-Origin': "*",
                                'Access-Control-Allow-Methods': 'GET, POST, OPTIONS, DELETE, PUT',
                                origin: true,
                                methods: 'GET,HEAD,PUT,PATCH,POST,DELETE',
                                credentials: true,
                                exposedHeaders: ['x-auth-token']
                            }}
                        />
                    </div>
                </div>
            </div>
        );
    }
}

export default withTranslation('common')(LogIn);
